import R from 'ramda'

import {
  FETCH_PHONE_BY_ID_SUCCESS
} from 'actionTypes'

const initialState = {
  id: null    //id  телефона этой страницы
}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_PHONE_BY_ID_SUCCESS:
      return R.merge(state, {
        id: R.prop("id", payload)  //получаем id из payload, это тоже самое, что и payload.id
      })
    default:
      return state
  }
}
