 import R from 'ramda'

 import {
   FETCH_PHONES_SUCCESS,
   LOAD_MORE_PHONES_SUCCESS,
   FETCH_PHONE_BY_ID_SUCCESS
 } from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_PHONES_SUCCESS:
      const newValues = R.indexBy(R.prop('id'), payload)  //превращаем [{},{}]  в {} с ключами-id, каждому из которых записан соответствующий телефон.(т.е. в данном {}: свойства- это id разных телефонов, а значения- {} из параметров телефона).
      return R.merge(state, newValues)
    case LOAD_MORE_PHONES_SUCCESS:
      const moreValues = R.indexBy(R.prop('id'), payload)
      return R.merge(state, moreValues)
    case FETCH_PHONE_BY_ID_SUCCESS:
      return R.assoc(payload.id, payload, state)  //в state добавить ключь-payload.id, значение ключа-payload
    default:
      return state
  }
}

